var tap = require("../"),
    test = tap.test;

test("parent", function (t) {
  // TODO: Make grandchildren tests count?
  t.plan(3);
  t.ok(true, 'p spec');
  t.test("subtest", function (t) {
    t.ok(true, 'ch spec');
    t.test('nested subtest', function(t) {
      t.ok(true, 'grch spec');
      t.end();
    });
    t.end();
  });
  t.test('another subtest', function(t) {
    t.ok(true, 'ch spec 2');
    t.end();
  });
  t.end();
})

