'use strict';

var grunt = require('grunt');

/*
  ======== A Handy Little Nodeunit Reference ========
  https://github.com/caolan/nodeunit

  Test methods:
    spec.expect(numAssertions)
    spec.done()
  Test assertions:
    spec.ok(value, [message])
    spec.equal(actual, expected, [message])
    spec.notEqual(actual, expected, [message])
    spec.deepEqual(actual, expected, [message])
    spec.notDeepEqual(actual, expected, [message])
    spec.strictEqual(actual, expected, [message])
    spec.notStrictEqual(actual, expected, [message])
    spec.throws(block, [error], [message])
    spec.doesNotThrow(block, [error], [message])
    spec.ifError(value)
*/

exports.mustache_html = {
  setUp: function(done) {
    // setup here if necessary
    done();
  },
  default_options: function(test) {
    test.expect(1);

    var actual = grunt.file.read('dist/default.html');
    var expected = grunt.file.read('spec/expected/default.html');
    test.equal(actual, expected, 'page created.');

    test.done();
  },
  custom_options: function(test) {
    test.expect(1);

    var actual = grunt.file.read('tmp/custom.html');
    var expected = grunt.file.read('spec/expected/custom.html');
    test.equal(actual, expected, 'page created.');

    test.done();;
  }
};
